mapper.select:
select * from lm_user_appraise t


    and t.id = #{id}


    and t.spu_id = #{spu_id}


    and t.sku_id = #{sku_id}


    and t.order_id = #{order_id}


    and t.user_id = #{user_id}


    and t.content = #{content}


    and t.score = #{score}


    and t.created_by = #{created_by}


    and t.created_time = #{created_time}


    and t.updated_by = #{updated_by}

select a.id as id, a.created_time as createdTime, a.updated_time as updatedTime, a.content as content, a.score as score, a.user_id as userId, u.nick_name as userNickName, u.avatar_url as userAvatarUrl, a.order_id as orderId, a.spu_id as spuId, a.sku_id as skuId, sk.title as skuTitle, sp.title as spuTitle from lm_user_appraise a LEFT JOIN lm_user u ON a.user_id = u.id LEFT JOIN lm_sku sk ON a.sku_id = sk.id LEFT JOIN lm_spu sp ON a.spu_id = sp.id where 1 = 1
AND a.id = #{id}
AND u.nick_name LIKE concat("%",#{userName},"%")
AND sp.title LIKE concat("%",#{spuName},"%")
AND a.order_id = #{orderId}
AND a.score = #{score}
AND a.content LIKE concat("%",#{content},"%")
order by a.id desc limit #{offset},#{limit}

select count(1) from lm_user_appraise a LEFT JOIN lm_user u ON a.user_id = u.id LEFT JOIN lm_sku sk ON a.sku_id = sk.id LEFT JOIN lm_spu sp ON a.spu_id = sp.id where 1 = 1
AND a.id = #{id}
AND u.nick_name LIKE concat("%",#{userName},"%")
AND sp.title LIKE concat("%",#{spuName},"%")
AND a.order_id = #{orderId}
AND a.score = #{score}
AND a.content LIKE concat("%",#{content},"%")
